lib/use-optimized-chat.ts.content:
"use client";

import { useState, useRef, FormEvent, ChangeEvent } from "react";
import { flushSync } from "react-dom";

type Message = {
  id: string;
  role: "user" | "assistant";
  content: string;
  parts?: any[];
};

type UseChatOptions = {
  api: string;
  body?: Record<string, any>;
  onError?: (error: Error) => void;
};

type ChatStatus = "ready" | "streaming" | "error";

export function useOptimizedChat(options: UseChatOptions) {
  const { api, body, onError } = options;

  const [messages, setMessages] = useState<Message[]>([]);
  const [input, setInput] = useState("");
  const [status, setStatus] = useState<ChatStatus>("ready");
  const [, forceUpdate] = useState({});
  const abortControllerRef = useRef<AbortController | null>(null);
  const messagesRef = useRef<Message[]>([]);

  const handleInputChange = (e: ChangeEvent<HTMLInputElement> | ChangeEvent<HTMLTextAreaElement>) => {
    setInput(e.target.value);
  };

  const append = async ({ role, content }: { role: "user" | "assistant"; content: string }) => {
    const userMessage: Message = {
      id: `user-${Date.now()}-${Math.random()}`,
      role,
      content,
    };

    // flushSync: natychmiastowe wyświetlenie wiadomości użytkownika
    flushSync(() => {
      setMessages((prev) => {
        const updated = [...prev, userMessage];
        messagesRef.current = updated;
        return updated;
      });
      setStatus("streaming");
    });

    try {
      abortControllerRef.current = new AbortController();
      const timestamp = Date.now();

      const response = await fetch(`${api}?_=${timestamp}`, {
        method: "POST",
        headers: { 
          "Content-Type": "application/json",
          "Cache-Control": "no-cache, no-store, must-revalidate",
          "Pragma": "no-cache",
        },
        body: JSON.stringify({
          messages: messagesRef.current,
          timestamp,
          ...body,
        }),
        signal: abortControllerRef.current.signal,
        cache: "no-store",
      });

      if (!response.ok) throw new Error(`HTTP ${response.status}`);

      const reader = response.body?.getReader();
      if (!reader) throw new Error("No reader");

      const decoder = new TextDecoder();
      let buffer = "";
      let currentTextMessageId: string | null = null;

      // Real-time streaming - proces każdą kompletną linię natychmiast
      while (true) {
        const { done, value } = await reader.read();
        if (done) break;

        // Dekoduj natychmiast bez buforowania
        buffer += decoder.decode(value, { stream: true });
        const lines = buffer.split('\n');
        buffer = lines.pop() || '';

        for (const line of lines) {
          if (!line.trim()) continue;

          try {
            const data = JSON.parse(line);
            console.log('[CLIENT PARSE]', data.type, Date.now());

            // TEXT DELTA - ABSOLUTNIE ZERO BATCHING
            if (data.type === "text-delta") {
              if (!currentTextMessageId) {
                const newMsg: Message = {
                  id: `text-${Date.now()}-${Math.random()}`,
                  role: "assistant",
                  content: data.delta,
                };
                currentTextMessageId = newMsg.id;

                // WYMUSZENIE synchronicznego update BEZ batching
                flushSync(() => {
                  messagesRef.current = [...messagesRef.current, newMsg];
                  setMessages([...messagesRef.current]);
                });
                // Dodatkowe wymuszenie rerenderu
                requestAnimationFrame(() => {
                  forceUpdate({});
                });
              } else {
                // NATYCHMIASTOWA aktualizacja - ZERO opóźnień
                flushSync(() => {
                  messagesRef.current = messagesRef.current.map((msg) =>
                    msg.id === currentTextMessageId
                      ? { ...msg, content: msg.content + data.delta }
                      : msg
                  );
                  setMessages([...messagesRef.current]);
                });
                // Dodatkowe wymuszenie rerenderu
                requestAnimationFrame(() => {
                  forceUpdate({});
                });
              }
            }

            // TOOL CALL START - ZERO BATCHING
            else if (data.type === "tool-call-start") {
              currentTextMessageId = null;
              const toolMsg: Message = {
                id: `tool-${data.toolCallId}-${Date.now()}`,
                role: "assistant",
                content: "",
                parts: [{
                  type: "tool-invocation",
                  toolInvocation: {
                    toolCallId: data.toolCallId,
                    toolName: "",
                    args: {},
                    argsText: "",
                    state: "streaming",
                  },
                }],
              };

              flushSync(() => {
                messagesRef.current = [...messagesRef.current, toolMsg];
                setMessages([...messagesRef.current]);
              });
              requestAnimationFrame(() => forceUpdate({}));
            }

            // TOOL NAME - ZERO BATCHING
            else if (data.type === "tool-name-delta") {
              flushSync(() => {
                messagesRef.current = messagesRef.current.map((msg) => {
                  if (msg.id.includes(data.toolCallId) && msg.parts?.[0]?.type === "tool-invocation") {
                    return {
                      ...msg,
                      parts: [{
                        ...msg.parts[0],
                        toolInvocation: {
                          ...msg.parts[0].toolInvocation,
                          toolName: data.toolName,
                        },
                      }],
                    };
                  }
                  return msg;
                });
                setMessages([...messagesRef.current]);
              });
              requestAnimationFrame(() => forceUpdate({}));
            }

            // TOOL ARGUMENTS - ZERO BATCHING
            else if (data.type === "tool-argument-delta") {
              flushSync(() => {
                messagesRef.current = messagesRef.current.map((msg) => {
                  if (msg.id.includes(data.toolCallId) && msg.parts?.[0]?.type === "tool-invocation") {
                    const currentArgsText = msg.parts[0].toolInvocation.argsText || "";
                    const newArgsText = currentArgsText + data.delta;
                    let parsedArgs = msg.parts[0].toolInvocation.args;
                    try {
                      parsedArgs = JSON.parse(newArgsText);
                    } catch (e) {
                      // Zachowaj stare args dopóki JSON nie jest kompletny
                    }
                    return {
                      ...msg,
                      parts: [{
                        ...msg.parts[0],
                        toolInvocation: {
                          ...msg.parts[0].toolInvocation,
                          argsText: newArgsText,
                          args: parsedArgs,
                        },
                      }],
                    };
                  }
                  return msg;
                });
                setMessages([...messagesRef.current]);
              });
              requestAnimationFrame(() => forceUpdate({}));
            }

            // TOOL INPUT AVAILABLE - ZERO BATCHING
            else if (data.type === "tool-input-available") {
              flushSync(() => {
                messagesRef.current = messagesRef.current.map((msg) => {
                  if (msg.id.includes(data.toolCallId) && msg.parts?.[0]?.type === "tool-invocation") {
                    return {
                      ...msg,
                      parts: [{
                        ...msg.parts[0],
                        toolInvocation: {
                          ...msg.parts[0].toolInvocation,
                          args: data.input,
                          state: "call",
                        },
                      }],
                    };
                  }
                  return msg;
                });
                setMessages([...messagesRef.current]);
              });
              requestAnimationFrame(() => forceUpdate({}));
            }

            // TOOL OUTPUT - ZERO BATCHING
            else if (data.type === "tool-output-available") {
              flushSync(() => {
                messagesRef.current = messagesRef.current.map((msg) => {
                  if (msg.id.includes(data.toolCallId) && msg.parts?.[0]?.type === "tool-invocation") {
                    return {
                      ...msg,
                      parts: [{
                        ...msg.parts[0],
                        toolInvocation: {
                          ...msg.parts[0].toolInvocation,
                          state: "result",
                          result: data.output,
                        },
                      }],
                    };
                  }
                  return msg;
                });
                setMessages([...messagesRef.current]);
              });
              requestAnimationFrame(() => forceUpdate({}));
            }
          } catch (e) {
            if (!(e instanceof SyntaxError)) throw e;
          }
        }
      }

      flushSync(() => {
        setStatus("ready");
      });
    } catch (error) {
      if (error instanceof Error && error.name === "AbortError") {
        setStatus("ready");
        return;
      }

      setStatus("error");
      if (onError && error instanceof Error) {
        onError(error);
      }
      console.error("Chat error:", error);
    }
  };

  const handleSubmit = async (e: FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    if (!input.trim() || status === "streaming") return;

    const userInput = input;
    setInput("");
    await append({ role: "user", content: userInput });
  };

  const stop = () => {
    if (abortControllerRef.current) {
      abortControllerRef.current.abort();
      abortControllerRef.current = null;
    }
    setStatus("ready");
  };

  return {
    messages,
    input,
    handleInputChange,
    handleSubmit,
    status,
    stop,
    append,
    setMessages: (msgs: Message[]) => {
      messagesRef.current = msgs;
      setMessages(msgs);
    },
  };
}

next.config.ts.content:
import type { NextConfig } from "next";

const nextConfig: NextConfig = {
  reactStrictMode: true,

  generateBuildId: async () => {
    return `no-cache-${Date.now()}-${Math.random().toString(36).substring(7)}`;
  },

  images: {
    unoptimized: true,
  },

  onDemandEntries: {
    maxInactiveAge: 0,
    pagesBufferLength: 0,
  },

  experimental: {
    optimizePackageImports: ['lucide-react', '@radix-ui/react-dialog', '@radix-ui/react-label', '@radix-ui/react-slot', '@radix-ui/react-tooltip'],
  },

  async headers() {
    return [
      {
        source: "/api/:path*",
        headers: [
          {
            key: "Cache-Control",
            value: "no-cache, no-store, no-transform, must-revalidate",
          },
          {
            key: "X-Accel-Buffering",
            value: "no",
          },
        ],
      },
      {
        source: "/:path*",
        headers: [
          {
            key: "Content-Security-Policy",
            value: [
              "default-src 'self'",
              "frame-src https://*.e2b.dev https://*.e2b.app https://va.vercel-scripts.com",
              "frame-ancestors 'self' https://*.e2b.dev https://*.e2b.app",
              "connect-src 'self' https://*.e2b.dev https://*.e2b.app",
              "img-src 'self' data: https://*.e2b.dev https://*.e2b.app",
              "script-src 'self' 'unsafe-inline' 'unsafe-eval' https://*.e2b.dev https://*.e2b.app https://va.vercel-scripts.com",
              "style-src 'self' 'unsafe-inline'",
            ].join("; "),
          },
          {
            key: "X-Frame-Options",
            value: "SAMEORIGIN",
          },
        ],
      },
    ];
  },
};

export default nextConfig;

middleware.ts.content:
import { NextResponse } from 'next/server'
import type { NextRequest } from 'next/server'

export function middleware(request: NextRequest) {
  const response = NextResponse.next()

  // ULTRA AGGRESSIVE CACHE BLOCKING - PERMANENT AND COMPLETE
  response.headers.set('Cache-Control', 'no-store, no-cache, must-revalidate, proxy-revalidate, max-age=0, s-maxage=0, no-transform, private')
  response.headers.set('Pragma', 'no-cache')
  response.headers.set('Expires', '-1')
  response.headers.set('Surrogate-Control', 'no-store')
  response.headers.set('CDN-Cache-Control', 'no-store')
  response.headers.set('Cloudflare-CDN-Cache-Control', 'no-store')
  response.headers.set('Vercel-CDN-Cache-Control', 'no-store')
  response.headers.set('Edge-Cache-Control', 'no-store')
  response.headers.set('Clear-Site-Data', '"cache", "cookies", "storage"')

  return response
}

export const config = {
  matcher: [
    '/((?!_next/static|_next/image|favicon.ico|.*\\.(?:svg|png|jpg|jpeg|gif|webp)$).*)',
  ],
}

components/prompt-suggestions.tsx.content:
import { ArrowUpRight } from "lucide-react";
import { Button } from "./ui/button";
import { memo } from "react";

const suggestions = [
  {
    text: "Bing",
    prompt: "Otwórz przeglądarkę i włącz https://www.bing.com/?cc=pl",
  },
  {
    text: "Oferta T-Mobile",
    prompt: "Znajdź ofertę Magenta TV od T-Mobile'",
  },
  {
    text: "Github",
    prompt: "Wejdź na Github",
  },
];

export const PromptSuggestions = memo(function PromptSuggestions({
  submitPrompt,
  disabled,
}: {
  submitPrompt: (prompt: string) => void;
  disabled: boolean;
}) {
  return (
    <div className="flex flex-wrap items-center gap-3 px-4">
      {suggestions.map((suggestion, index) => (
        <Button
          key={index}
          variant="pill"
          size="pill"
          onClick={() => submitPrompt(suggestion.prompt)}
          disabled={disabled}
        >
          <span>
            <span className="text-black text-sm">
              {suggestion.text.toLowerCase()}
            </span>
          </span>
          <ArrowUpRight className="ml-1 h-2 w-2 sm:h-3 sm:w-3 text-zinc-500 group-hover:opacity-70" />
        </Button>
      ))}
    </div>
  );
});

