components/prevent-chat-caching.tsx.content:
"use client";

import { useEffect } from "react";

export function PreventChatCaching() {
  useEffect(() => {
    if (typeof window === "undefined") return;

    // PERMANENTLY BLOCK CHAT MESSAGE CACHING - NOT DELETION, JUST CACHING

    // Override localStorage.setItem to block chat-related keys from being cached
    const originalLocalStorageSetItem = Storage.prototype.setItem;
    Storage.prototype.setItem = function(key: string, value: string) {
      const lowerKey = key.toLowerCase();
      const chatKeywords = [
        'chat', 'message', 'conversation', 'ai-', 'vercel', 
        'stream', 'history', 'dialog', 'assistant', 'prompt'
      ];

      const isChatRelated = chatKeywords.some(keyword => lowerKey.includes(keyword));

      if (isChatRelated) {
        console.warn(`[CHAT CACHE BLOCKED] Prevented caching of: ${key}`);
        return; // Block caching, but don't delete from memory
      }

      return originalLocalStorageSetItem.call(this, key, value);
    };

    // Override sessionStorage.setItem to block chat-related keys
    const originalSessionStorageSetItem = sessionStorage.__proto__.setItem;
    sessionStorage.__proto__.setItem = function(key: string, value: string) {
      const lowerKey = key.toLowerCase();
      const chatKeywords = [
        'chat', 'message', 'conversation', 'ai-', 'vercel', 
        'stream', 'history', 'dialog', 'assistant', 'prompt'
      ];

      const isChatRelated = chatKeywords.some(keyword => lowerKey.includes(keyword));

      if (isChatRelated) {
        console.warn(`[CHAT CACHE BLOCKED] Prevented session caching of: ${key}`);
        return;
      }

      return originalSessionStorageSetItem.call(this, key, value);
    };

    // Block IndexedDB for chat data
    if (window.indexedDB) {
      const originalIDBOpen = indexedDB.open;
      indexedDB.open = function(name: string, version?: number) {
        const lowerName = name.toLowerCase();
        const chatKeywords = ['chat', 'message', 'conversation', 'ai', 'vercel'];

        if (chatKeywords.some(keyword => lowerName.includes(keyword))) {
          console.warn(`[CHAT CACHE BLOCKED] Prevented IndexedDB: ${name}`);
          throw new Error(`Chat caching blocked for: ${name}`);
        }

        if (version !== undefined) {
          return originalIDBOpen.call(this, name, version);
        }
        return originalIDBOpen.call(this, name);
      };
    }

    // Add headers to fetch requests for chat endpoints to prevent server-side caching
    const originalFetch = window.fetch;
    window.fetch = function(...args) {
      const [resource, config] = args;
      let url = '';
      if (typeof resource === 'string') {
        url = resource;
      } else if (resource instanceof Request) {
        url = resource.url;
      } else if (resource instanceof URL) {
        url = resource.toString();
      }

      // Check if this is a chat-related API call
      const isChatAPI = url.includes('/chat') || 
                        url.includes('/message') || 
                        url.includes('/conversation') ||
                        url.includes('/api/chat');

      if (isChatAPI) {
        const newConfig = {
          ...config,
          cache: "no-store" as RequestCache,
          headers: {
            ...((config && config.headers) || {}),
            "Cache-Control": "no-cache, no-store, must-revalidate, max-age=0",
            "Pragma": "no-cache",
            "Expires": "0",
            "X-No-Cache": "true",
            "X-Chat-No-Persist": "true",
          },
        };
        return originalFetch(resource, newConfig);
      }

      return originalFetch(...args);
    };

    console.log('[CHAT CACHING BLOCKED] All chat message caching permanently disabled');

  }, []);

  return null;
}

lib/use-raw-streaming.ts.content:
"use client";

import { useState, useRef } from "react";
import { flushSync } from "react-dom";

type Message = {
  id: string;
  role: "user" | "assistant";
  content: string;
  parts?: any[];
};

type UseRawStreamingOptions = {
  api: string;
  body?: Record<string, any>;
  onError?: (error: Error) => void;
};

export function useRawStreaming(options: UseRawStreamingOptions) {
  const { api, body, onError } = options;

  const [messages, setMessages] = useState<Message[]>([]);
  const [input, setInput] = useState("");
  const [isStreaming, setIsStreaming] = useState(false);
  const abortControllerRef = useRef<AbortController | null>(null);
  const messagesRef = useRef<Message[]>([]);
  const currentTextIdRef = useRef<string | null>(null);
  const forceUpdateRef = useRef(0);

  const processLine = (line: string) => {
    if (!line.trim()) return;

    try {
      const data = JSON.parse(line);
      console.log('[STREAMING EVENT]', data.type, new Date().toISOString());

      const now = Date.now();
      let updated = false;

      // TEXT DELTA - NATYCHMIASTOWA AKTUALIZACJA
      if (data.type === "text-delta") {
        if (!currentTextIdRef.current) {
          const newMsg: Message = {
            id: `text-${now}-${Math.random()}`,
            role: "assistant",
            content: data.delta,
          };
          currentTextIdRef.current = newMsg.id;
          messagesRef.current = [...messagesRef.current, newMsg];
        } else {
          messagesRef.current = messagesRef.current.map((msg) =>
            msg.id === currentTextIdRef.current
              ? { ...msg, content: msg.content + data.delta }
              : msg
          );
        }
        updated = true;
      }

      // TOOL CALL START
      else if (data.type === "tool-call-start") {
        currentTextIdRef.current = null;
        const toolMsg: Message = {
          id: `tool-${data.toolCallId}-${now}`,
          role: "assistant",
          content: "",
          parts: [{
            type: "tool-invocation",
            toolInvocation: {
              toolCallId: data.toolCallId,
              toolName: "",
              args: {},
              argsText: "",
              state: "streaming",
            },
          }],
        };
        messagesRef.current = [...messagesRef.current, toolMsg];
        updated = true;
      }

      // TOOL NAME
      else if (data.type === "tool-name-delta") {
        messagesRef.current = messagesRef.current.map((msg) => {
          if (msg.id.includes(data.toolCallId) && msg.parts?.[0]?.type === "tool-invocation") {
            return {
              ...msg,
              parts: [{
                ...msg.parts[0],
                toolInvocation: {
                  ...msg.parts[0].toolInvocation,
                  toolName: data.toolName,
                },
              }],
            };
          }
          return msg;
        });
        updated = true;
      }

      // TOOL ARGUMENTS - KLUCZOWE DLA REAL-TIME STREAMING
      else if (data.type === "tool-argument-delta") {
        messagesRef.current = messagesRef.current.map((msg) => {
          if (msg.id.includes(data.toolCallId) && msg.parts?.[0]?.type === "tool-invocation") {
            const currentArgsText = msg.parts[0].toolInvocation.argsText || "";
            const newArgsText = currentArgsText + data.delta;
            let parsedArgs = msg.parts[0].toolInvocation.args;
            try {
              parsedArgs = JSON.parse(newArgsText);
            } catch (e) {
              // Keep old args
            }
            return {
              ...msg,
              parts: [{
                ...msg.parts[0],
                toolInvocation: {
                  ...msg.parts[0].toolInvocation,
                  argsText: newArgsText,
                  args: parsedArgs,
                },
              }],
            };
          }
          return msg;
        });
        updated = true;
      }

      // TOOL INPUT AVAILABLE
      else if (data.type === "tool-input-available") {
        messagesRef.current = messagesRef.current.map((msg) => {
          if (msg.id.includes(data.toolCallId) && msg.parts?.[0]?.type === "tool-invocation") {
            return {
              ...msg,
              parts: [{
                ...msg.parts[0],
                toolInvocation: {
                  ...msg.parts[0].toolInvocation,
                  args: data.input,
                  state: "call",
                },
              }],
            };
          }
          return msg;
        });
        updated = true;
      }

      // TOOL OUTPUT
      else if (data.type === "tool-output-available") {
        messagesRef.current = messagesRef.current.map((msg) => {
          if (msg.id.includes(data.toolCallId) && msg.parts?.[0]?.type === "tool-invocation") {
            return {
              ...msg,
              parts: [{
                ...msg.parts[0],
                toolInvocation: {
                  ...msg.parts[0].toolInvocation,
                  state: "result",
                  result: data.output,
                },
              }],
            };
          }
          return msg;
        });
        updated = true;
      }

      // FINISH
      else if (data.type === "finish") {
        setIsStreaming(false);
      }

      // ERROR
      else if (data.type === "error") {
        setIsStreaming(false);
        if (onError) {
          onError(new Error(data.errorText || "Streaming error"));
        }
      }

      // NATYCHMIASTOWY RERENDER - WYMUSZONY SYNC (BEZ BATCHING)
      if (updated) {
        forceUpdateRef.current++;
        // flushSync - wymusza natychmiastowy render, BLOKUJE React batching
        flushSync(() => {
          setMessages([...messagesRef.current]);
        });
      }

    } catch (e) {
      if (!(e instanceof SyntaxError)) {
        console.error('[PARSE ERROR]', e);
      }
    }
  };

  const send = async (userMessage: string) => {
    const userMsg: Message = {
      id: `user-${Date.now()}-${Math.random()}`,
      role: "user",
      content: userMessage,
    };

    messagesRef.current = [...messagesRef.current, userMsg];
    setMessages([...messagesRef.current]);
    setIsStreaming(true);
    currentTextIdRef.current = null;

    const abortController = new AbortController();
    abortControllerRef.current = abortController;

    try {
      // FETCH API Z READABLESTREAM - NAJLEPSZE DLA STREAMING
      const response = await fetch(`${api}?_=${Date.now()}`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "Cache-Control": "no-cache",
        },
        body: JSON.stringify({
          messages: messagesRef.current,
          timestamp: Date.now(),
          ...body,
        }),
        signal: abortController.signal,
      });

      if (!response.ok) {
        throw new Error(`HTTP ${response.status}`);
      }

      const reader = response.body?.getReader();
      if (!reader) {
        throw new Error("No response body");
      }

      const decoder = new TextDecoder();
      let buffer = "";

      // CZYTAJ STREAM W PĘTLI - REAL-TIME
      while (true) {
        const { done, value } = await reader.read();

        if (done) {
          // Przetwórz ostatnie dane w buforze
          if (buffer.trim()) {
            processLine(buffer);
          }
          break;
        }

        // Dodaj nowe dane do bufora
        buffer += decoder.decode(value, { stream: true });

        // Podziel na linie i przetwórz
        const lines = buffer.split('\n');
        buffer = lines.pop() || '';

        for (const line of lines) {
          if (line.trim()) {
            processLine(line);
          }
        }
      }

      setIsStreaming(false);

    } catch (error) {
      if (error instanceof Error && error.name === 'AbortError') {
        console.log('[STREAMING] Aborted');
      } else {
        console.error('[STREAMING ERROR]', error);
        if (onError) {
          onError(error instanceof Error ? error : new Error(String(error)));
        }
      }
      setIsStreaming(false);
    }
  };

  const stop = () => {
    if (abortControllerRef.current) {
      abortControllerRef.current.abort();
      abortControllerRef.current = null;
    }
    setIsStreaming(false);
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (!input.trim() || isStreaming) return;
    const userInput = input;
    setInput("");
    send(userInput);
  };

  return {
    messages,
    input,
    setInput,
    handleSubmit,
    isStreaming,
    stop,
    send,
    setMessages: (msgs: Message[]) => {
      messagesRef.current = msgs;
      setMessages(msgs);
    },
  };
}

